mapper.select:
SELECT user_id
FROM   USERS
WHERE  user_id = #{user_id}

SELECT auth_num
FROM   AUTHMAIL
WHERE  user_mail = #{user_mail}

mapper.update:
UPDATE AUTHMAIL
SET    auth_num = #{auth_num}
WHERE  user_mail = #{user_mail}

mapper.select:
SELECT *
FROM   USERS
WHERE  user_id = #{user_id}
AND    user_pw = #{user_pw}

mapper.update:
UPDATE BOARDER
SET title = #{title}, contents =#{contents}
WHERE boarder_id = #{boarder_id}

UPDATE UPLOADFILE
SET file_name = #{file_name}, file_realName =#{file_realName}
WHERE boarder_id = #{boarder_id}
